mapper.select:
select /* com.cj.freshway.fs.cps.common.fscpsuserInfo.FscpsUserInfoMapper.selectMenuList */ * from ( select a.prog_cd as prog_cd, case when b.prog_nm is null then a.prog_nm else b.prog_nm end as prog_nm, substring(a.prog_no::varchar, 1, length(a.prog_no)-2) as up_prog_no, a.prog_no as prog_no, #{progLvl} - 3 as prog_lvl, a.prog_url as prog_url, a.prog_url_args as prog_url_args from fscps.cm_program_m a left outer join fscps.cm_program_language_m b on a.prog_cd = b.prog_cd and b.lang_cl = 'ko' where 1 = 1 and a.use_yn = 'Y' and a.menu_yn = 'Y' and a.system_cl = 'FSBO'
and a.prog_lvl = #{progLvl} order by a.prog_no) tb where 1 = 1
and tb.up_prog_no = #{upProgNo}

    SELECT COUNT(*)
		FROM CC_CMNT_D
		WHERE REGR_ID = #{id}
